import { useState } from "react";

const BasicForm = props => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");

  const nameIsValid =
    firstName.trim() !== "" ||
    (lastName.trim() !== "" && firstNameIsTouched) ||
    lastNameIsTouched;
  const [emailIsValid, setEmailIsValid] = useState(false);

  const [firstNameIsTouched, setFirstNameIsTouched] = useState(false);
  const [lastNameIsTouched, setLastNameIsTouched] = useState(false);
  const [emailIsTouched, setEmailIsTouched] = useState(false);

  const validateName = name => {
    if (name.trim() === "") {
      setNameIsValid(false);
    }
  };

  const validateEmail = email => {
    if (!/\S+@\S+\.\S+/.test(email)) {
      setEmailIsValid(false);
    }
  };

  const firstNameChangeHandler = e => {
    setFirstNameIsTouched(true);
    setFirstName(e.target.value);
    validateName(e.target.value);
  };
  const lastNameChangeHandler = e => {
    setLastName(e.target.value);
    validateName(e.target.value);
  };
  const emailChangeHandler = e => {
    setEmail(e.target.value);
    validateEmail(e.target.value);
  };

  const formSubmitHandler = e => {
    e.preventDefault();
  };

  const nameInputClasses = !nameIsValid
    ? "form-control invalid"
    : "form-control";

  const emailInputClasses = !emailIsValid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={formSubmitHandler}>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input
            className={nameInputClasses}
            type="text"
            id="name"
            onChange={firstNameChangeHandler}
            onBlur={validateName}
          />
          {!nameIsValid && <p>Please enter a valid name</p>}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input
            className={nameInputClasses}
            type="text"
            id="name"
            onChange={lastNameChangeHandler}
          />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input
          className={emailInputClasses}
          type="text"
          id="name"
          onChange={emailChangeHandler}
        />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
